      <input
        id={props.id}
        type={props.type}
        placeholder={props.placeholder}
        onChange={changeHandler}
      ></input>
    ) : (
      <textarea id={props.id} row={props.rows || 3} onChange={changeHandler} />
    );

  return (
    <div className={`form-control`}>
      <label htmlFor={props.id}>{props.label}</label>
      {element}
