import React, { useEffect } from "react";

import "./Map.css";

const Map = (props) => {
    const {center,zoom}=props;
  useEffect(() => {

    const map = new window.google.maps.Map(
      document.getElementById("map-render"),
      {
        center: center,
        zoom: zoom
      }
    );
    new window.google.map.Marker({ position: props.center, map: map });
  }, [center,zoom]); //this function will be re-executed when any of the parameters is changed



  return (
    <div
      id="map-render"
      className={`map ${props.className}`}
      style={props.style}
    ></div>
  );
};

export default Map;
